INSERT INTO `ss_placard` (`id`, `title`, `create_time`, `public_time`, `content`, `status`, `is_del`) VALUES
(1, 'JS(去掉前后空格或去掉所有空格)的用法', 1674728687, 1674728586, '<p style=\"text-align:justify;text-indent:2em;\">在使用 js 处理字符串时，难免会在字符串的头部或未尾留有一些空格，如果这些头部或尾部的空格没有实际的用处的话，我们就要想方设法的把他们去掉，以免他影响到正常的字符串。</p><p style=\"text-align:justify;text-indent:2em;\">关于 JS 去掉字符串头部和尾部的空格有很多种方法，下面飞鸟慕鱼就来简单的说几种。</p>', 0, 0),
(2, '升空起飞！他们在战位度新春', 1674728795, 1674728778, '<p style=\"text-align:justify;text-indent:2em;\" class=\"art_p\" cms-style=\"font-L\">寒潮涌动，砺兵火热</p><p style=\"text-align:justify;text-indent:2em;\" class=\"art_p\" cms-style=\"font-L\">春节来临之际</p><p style=\"text-align:justify;text-indent:2em;\" class=\"art_p\" cms-style=\"font-L\">东部战区空军航空兵某团机场上</p><p style=\"text-align:justify;text-indent:2em;\" class=\"art_p\" cms-style=\"font-L\">战机列阵，铁翼掠空</p><p style=\"text-align:justify;text-indent:2em;\" class=\"art_p\" cms-style=\"font-L\">多个训练课目同场展开——</p>', 0, 0),
(3, '中国传统色打开锦绣山河', 1674728894, 1674728804, '<p>一、春暖花开，用传统色打开锦绣山河<br/></p><p><br/>春暖花开，万物复苏。此时此刻，我们可以用中国传统的五颜六色来打开锦绣山河，感受大好河山的壮丽。从海上看，五彩斑斓的海市蜃楼在海浪中闪耀；从山上看，金黄的小麦地里有一片片的碧绿；从田野看，五彩斑斓的水库里有一片片的彩云。这些都是中国传统色彩带来的壮丽。</p>', 0, 0),
(4, '兔年新春中国消费市场加速回暖', 1674728926, 1674728896, '<p style=\"text-align:left;text-indent:2em;\">时隔两年，在广州工作的长沙市民张先生踏上期盼已久的回家过年旅途。出站后的第一件事就是“打卡”家乡奶茶品牌茶颜悦色。</p><p style=\"text-align:left;text-indent:2em;\">在店员的推荐下，他买了冬季新品“三层被”，开心地发了朋友圈：“回家了！目标：好好陪家人，吃遍家乡美食。”</p><p style=\"text-align:left;text-indent:2em;\">“门店客流量和平时相比翻倍增长。”茶颜悦色高铁南站店店长陈坤和他一样开心。</p><p style=\"text-align:left;text-indent:2em;\">今年春节是中国疫情防控进入新阶段后的首个春节。春节“返乡潮”叠加各地出台的刺激消费政策，使得兔年春节的旅游、消费市场更为火热。</p>', 0, 0),
(5, '明年再见!充电器一拔我和浪浪山道别了引热议 专家建议春节...', 1674728982, 1674728963, '<p style=\"text-align:start;text-indent:2em;\">一眨眼春节就快过完了，大家收拾好行囊，又踏上了离家返程的旅途，酒没喝尽，话没说完，许多事情还没做，很多人还没有好好告别，纵使万般不舍，泪在心田，充电器一拔，再见又是新的一年。</p><p style=\"text-align:start;text-indent:2em;\">这也引发了网友的热议，有网友表示，不是我说，春节假期是真的太短了，七天时间，对于长途的人来说，收尾路上就要去掉两天，一共只有五天啊。</p><p style=\"text-align:start;text-indent:2em;\">也有网友表示，看得人好难受，下一个节假日什么时候？清明端午吗，好想多陪陪父母啊。新年假期即将结束，万般不舍也得离开。</p>', 0, 0),
(6, 'JS(去掉前后空格或去掉所有空格)的用法', 1674728687, 1674728586, '<p style=\"text-align:justify;text-indent:2em;\">在使用 js 处理字符串时，难免会在字符串的头部或未尾留有一些空格，如果这些头部或尾部的空格没有实际的用处的话，我们就要想方设法的把他们去掉，以免他影响到正常的字符串。</p><p style=\"text-align:justify;text-indent:2em;\">关于 JS 去掉字符串头部和尾部的空格有很多种方法，下面飞鸟慕鱼就来简单的说几种。</p>', 0, 0),
